void CPMFooFrame::on_button_dummy(wxCommandEvent &event)
{
    wxMessageBox("You hit me!");
    ACE_DEBUG((LM_DEBUG, "You hit me!\n")); // To test console
}

void CPMFooFrame::on_checkbox_console(wxCommandEvent &event)
{
    if (checkbox_console->GetValue())
    {
        console_.show(true);
    }
    else
    {
        console_.show(false);
    }
}

void CPMFooFrame::on_close(wxCloseEvent& event)
{
    if (app_layer_->as_server())
    {
        this->Show(false);
        console_.show(false);
        checkbox_console->SetValue(false);
        app_layer_->as_gui(false);
    }
    else
    {
        this->Destroy();
    }
}


